import React, {useState} from "react";
import './App.css';

const IntroState = () => {
  const [tasks, setTasks] = useState([
    {task: "shopping", status: 33},
    {task: "cooking", status:12},
    {task: "cleaning", status: 13}
  ]);

  const [completed,setStatus] = useState(true);

  const greeting = (value) => {
    alert("hello" + value);
  };

  return (
    <div>
      {!completed ? (
        <div>
          <Task task = {tasks[0].task} func={greeting} />
          <button onClick={() => setStatus(true)}>complete</button>
        </div>

      ) : (
        <div className=".item">
          <Task task = {tasks[0].task} func={greeting} />
          <button onClick={() => setStatus(false)}>in progress</button>
        </div>
      )}
    </div>
  )
}

const Task = (props) => {
  return (
    <div>
      <h1 onclick={() => props.func(props.task)}>{props.task}</h1>
    </div>
  )
}

export default IntroState

