import './App.css';
import React from 'react';
import logo from  './images/1.png';


const App = () => {
  return (
    <div className="article">
      <Article
        title="Don't think the pandemic is over, whitty warns"
        subtitle="Unlocking too quickly would lead to a substantial surge in infection, UK chief medical adviser says."
        time="2h"
        category="health"
      />
      <Article 
        title="Pay rise was set to be 2.1% - NHS chief"
        subtitle="NHS England boss backs ministers over pay dispute but does not rule 
        out a one-off bonus for workers."
        time="1h"
        category="UK Politics"
      />
      <Article
        title="Charity criticises  Morgan's comments about Meghan"
        subtitle="Mental health charity Mind says it is 'dissapointed' by comments Piers Morgan made on Monday"
        time="2h"
        category='entertainment & arts'
      />
      <Article
        title="Unilever drops word 'normal' from beauty products"
        subtitle="The owner of Dove and Vaseline will remove the word from about 200 products in a push for inclusivity."
        time="10m"
        category="Business"
      />
    </div>
  )
}

const Article = ({title, subtitle, time, category}) => {
  return(
    <div className="container1">
      <img src={logo} width="100%"/>
      <h2>{title}</h2>
      <p>{subtitle}</p>
      <div className="footnotes">
        <p className="footnotes">{time}</p>
        <p className="footnotes">{category}</p>
      </div>
    </div>
  )
}




export default App;
