/////////////map method - using state////////
//this is the same as below method, but using state instead of render()
import React from 'react'
class App extends React.Component {
  state = { //Sometimes you will stores arrays and stuff in the state. this is how.
    bookPrices: [2.5, 4.5, 3.5, 6.0, 8.0]
  }

  render() {
    return (
      <div>
        {this.state.bookPrices.map((price, index) => {
          return (
          <div key={index}> {/*If map function returns multiple elements,*/}
            <h1>I am h1 element</h1>
            <p>i am p element</p>
            <p>{price}</p>
            <button>Button</button>
          </div>)
        })}
      </div>
    )
  }
}

export default App

