const App = () => {
  const [num, setNum] = useState(0)
  const minusHandler = () => {
    setNum(num -1)
  }
  return (
    <div>
      <h1>{num}</h1>
      <button onClick={() => setNum(num + 1)}>add one</button>
      <button onClick={() => setNum(num + 1)}>minus one</button>
    </div>
  )
}

export default App
