// //////////////state demonstration 1/////////////////////

import './App.css';
import React from 'react';

class App extends React.Component {
  state = {  //state is a property of the class
    persons: [ //persons is a property of state. Holds an array of 2 objects.
      {name: "Dan", age: 34},
      {name: "Ben", age: 21}
    ]
  }

  render() {
    return (
      <div>
        <h1>I'm a class component</h1>
        <p>{this.state.persons[1].age}</p> {/* returns age of 2nd persons. (21)*/}        
      </div>
    );
  }
}
