import React from 'react'


const MapMethod = () => {
  const people = ["joe", "tom", "bill"]
  return (
    <div>
      <h1>map method</h1>
      {people.map((person) =>{
        return <p>person}</p>
      })}
    </div>
  )
}

export default MapMethod
