import './App.css';
import React from 'react'




const Person = (props) => {
  return (
    <div>
      <p> Hi i'm {props.name}</p>
      <Pet pet={props.petsName}/>
      <Job job={props.jobTitle}/>
    </div>
  )
}

const Pet = (props) => {
  return <p>My pets name is {props.pet}</p>
}

const Job = (props) => {
  return <p>I'm a {props.job}</p>
}


//import React from react. Not necessary with functioal components
class App extends React.Component {
  render() {
    return (
      <div>
        <h1> I'm a class component</h1>
        <Person name="Dan" age="34" job="Junior Developer" />
        <Person name="Stuart" age="30-something" />
        <Person name="Ben" age="21" petsName="john" type="dog"/>
      </div>
    )
  }
}

// export default App; //export current file


export default App;
