///////////todo list ATTEMPT 2/////////////////
import { useState } from "react";
const App = () => {
  const [list, setList] = useState([]);
  const [userInput, setUserInput] = useState("");

  const updateInput = (e) => {
    setUserInput(e.target.value);
  };
  const addHandler = (e) => {
    const newArr = [...list];
    newArr.push(userInput);
    setList(newArr);
    setUserInput("")
  };
  const removeHandler = (index) => {
    const newArr = [...list];
    newArr.splice(index, 1);
    setList(newArr);
  };

  return (
    <div>
      <h1>todo list</h1>
      <input type="text" value={userInput} onChange={updateInput} />
      <button onClick={addHandler}>add</button>
      <h2>{userInput}</h2>
      {list.map((item, index) => {
        return <Card index={index} removeHandler={removeHandler} key={index} item={item} />;
      })}
    </div>
  );
};

const Card = (props) => {
  return (
    <div>
      <h2>{props.item}</h2>
      <button onClick={() => props.removeHandler(props.index)}>remove</button>
    </div>
  );
};
export default App;

