import React from 'react'
class App extends React.Component {
  render() {
    const bookPrices = [2.5, 4.5, 3.5, 6.9, 8.0] //variables, functions and stuff get declared in render()

  return (
    <div>
      {bookPrices.map(price => {
        return <h1>The price is {price}</h1>;
      })}
    </div>
  )
  }
}

