import React, {useState} from "react";

const App = () => {
  const [persons, setPersons] = useState([
    {name: "Leon"}
  ])

  const handleClick = () => {
    console.log("Clickity click");
  }

  return (
    <div>
      <Person name={persons[0].name} clickMe={handleClick}/>
      <Person name={persons[1].name} clickMe={handleClick}/>
    </div>
  )
}

const Person = (props) => {
  return (
    // <h1 onClick={props.clickMe}>{props.name}</h1> //prints "clickety click" upon click of "Jordan" text.
    <h1 onClick={() => props.clickMe("Hi there")}>{props.name}</h1> //This is how to pass a value to a function on click, which is handled slightly differnetly. this is called an anonymous function.
  )

}


